import React, { Component } from 'react'
import Axios from 'axios'
import { KONEKSI } from '../connection'
import moment from 'moment'

export default class ListBlog extends Component {

    constructor(props){
        super(props)

        this.state = {
            list: []
        }

    }

    componentDidMount(){
        this.getUserBlog()
    }

    getUserBlog = () => {

        Axios.get(`${KONEKSI}/blog/userBlog/`+this.props.user)
        .then((res) => {
            alert("Delete Success")
            this.setState({list: res.data})
        }).catch((err) => {
            console.log(err)
        })


    }

    renderList = () => {

        var { list } = this.state

        if(list.length > 0){

            const data = list.map((item) => {

                var limit = 150;
                var desc = item.deskripsi;

                return (
                    <tr>
                        <td>{item.judul}</td>
                        <td width="250">{`${desc.length > limit ? desc.substring(0, limit)+"...": desc}`}</td>
                        <td>{moment(item.created_at).format("YYYY/MMM/DD")}</td>
                        <td className="text-center" width="100"><button className="btn btn-warning btn-sm"><i className="fa fa-edit"></i></button>{' '}
                            <button className="btn btn-danger btn-sm" onClick={() => this.deleteBlog(item.id)}><i className="fa fa-trash"></i></button></td>
                    </tr>
                )

            })
            return data

        }

    }

    deleteBlog = (id) => {

        var valid = window.confirm("Are you sure want to delete this?")
        if(valid){
            Axios.post(`${KONEKSI}/blog/deleteBlog`, {id:id})
            .then((res) => {
                this.getUserBlog()
            }).catch((err) => {
                console.log(err)
            })

        }else{
            return false
        }

    }

    render() {
        return (
            <div>
                <table className="table table-bordered">
                    <thead className="text-center">
                        <tr>
                            <th>Judul</th>
                            <th>Deskripsi</th>
                            <th>Created At</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.renderList()}
                    </tbody>
                </table>
            </div>
        )
    }
}
