import React, { Component } from 'react'
import Axios from 'axios'
import { KONEKSI } from '../connection'

export default class ListBlog extends Component {

    constructor(props){
        super(props)

        this.state = {
            list: []
        }

    }

    componentDidMount(){
        this.getUserBlog()
    }

    getUserBlog = () => {

        Axios.get(`${KONEKSI}/blog/userBlog/`+this.props.user)
        .then((res) => {
            this.setState({list: res.data})
        }).catch((err) => {
            console.log(err)
        })

    }

    renderList = () => {

        var { list } = this.state

        if(list.length > 0){

            const data = list.map((item) => {

                return (
                    <tr>
                        <td>{item.judul}</td>
                        <td>{item.deskripsi}</td>
                        <td>{item.created_at}</td>
                        <td><button className="btn btn-success"></button></td>
                    </tr>
                )

            })
            return data

        }

    }

    render() {
        return (
            <div>
                <table className="table table-bordered">
                    <thead className="text-center">
                        <tr>
                            <th>Judul</th>
                            <th>Deskripsi</th>
                            <th>Created At</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.renderList()}
                    </tbody>
                </table>
            </div>
        )
    }
}
